import React, { useState, useEffect } from 'react';

function Autocomplete() {
  const [input, setInput] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);

  useEffect(() => {
    if (input.trim() !== '') {
      fetch(`https://api.datamuse.com/sug?s=${input}`)
        .then(response => response.json())
        .then(data => {
          setSuggestions(data);
          setShowSuggestions(data.length > 0);
        })
        .catch(error => console.error('Error fetching suggestions:', error));
    } else {
      setSuggestions([]);
      setShowSuggestions(false);
    }
  }, [input]);

  const handleInputChange = (event) => {
    setInput(event.target.value.toLowerCase());
  };

  const handleSuggestionClick = (word) => {
    setInput(word);
    setShowSuggestions(false);
  };

  const handleClickOutside = (event) => {
    const searchInput = document.getElementById('default-search');
    const resultsContainer = document.getElementById('search-results');
    if (!searchInput.contains(event.target) && !resultsContainer.contains(event.target)) {
      setShowSuggestions(false);
    }
  };

  useEffect(() => {
    document.addEventListener('click', handleClickOutside);
    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, []);

  return (
    <div>
      <input
        type="search"
        id="default-search"
        className="block w-full p-4 ps-10 text-sm text-gray-900 border border-gray-300 rounded-lg bg-gray-50 focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
        placeholder="Search"
        value={input}
        onChange={handleInputChange}
        required
      />
      {showSuggestions && (
        <ul className="list-none p-0 m-0 absolute top-full left-0 w-full bg-white border border-gray-300 border-t-0 text-black dark:text-white dark:bg-gray-700 rounded-lg">
          {suggestions.map((item, index) => (
            <li key={index} onClick={() => handleSuggestionClick(item.word)}>
              {item.word}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Autocomplete;
