import React from 'react';

const Article = ({ article }) => {
  return (
    <div className="max-w-[1296px] mx-auto flex flex-col gap-2 p-24">
      <div className="flex">
        <div className="flex-1">
          <img src={article.urlToImage} alt="Article Image" />
          <h1 className="text-3xl font-bold mb-4 py-3">{article.title}</h1>
          <p className="text-sm dark:text-white text-gray-600 italic py-2">
            Analysis by {article.author} {article.publishedAt}
          </p>
          <div id="content">
            {content.split('.').map((sentence, index) => (
              <p key={index} className="font-serif text-base pb-5">
                {sentence.trim()}.
              </p>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Article;
