import React, { useState, useEffect } from 'react';

function ProfileCard() {
    const [user, setUser] = useState(null);
    const [editingField, setEditingField] = useState(null);

    useEffect(() => {
        const username = localStorage.getItem('name');
        fetchUserData(username)
            .then(userData => {
                setUser(userData);
            })
            .catch(error => {
                console.error('Error fetching user data:', error);
            });
    }, []);

    async function fetchUserData(username) {
        try {
            const response = await fetch(`$http://10.0.0.12:3000/api/userdata?username=${username}`);
            if (!response.ok) {
                throw new Error('Failed to fetch user data');
            }
            return await response.json();
        } catch (error) {
            console.error('Error fetching user data:', error);
            return null;
        }
    }

    async function saveField(fieldName, value) {
        try {
            const response = await fetch(`http://10.0.0.12:3000/api/updateUserData`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    username: user.username,
                    field: fieldName,
                    value: value,
                }),
            });

            if (!response.ok) {
                throw new Error('Failed to update user data');
            }

            setUser(prevUser => ({
                ...prevUser,
                [fieldName]: value,
            }));

            setEditingField(null);
        } catch (error) {
            console.error('Error updating user data:', error);
        }
    }

    function openEditing(fieldName) {
        setEditingField(fieldName);
    }

    function renderProfileCard() {
        if (!user) return null;

        const { username, jobTitle, bio, country, phone, email } = user;

        return (
            <div className="card-container bg-white border p-3 border-l-4 border-black dark:bg-gray-800 dark:text-white md:w-96 lg:w-80 xl:w-96 ml-0"
                style={{ top: '30%', left: '10%', transform: 'translate(-5%, -30%)' }}>
