function SitingScreen({ username, password, jobTitle, bio, country, phone, email, editingField, openEditing, saveField }) {
    return (
        <div id="Siting" className="hidden fixed inset-0 bg-black bg-opacity-50 overflow-y-auto h-screen">
            <div className="flex justify-center items-center mt-20">
                <div className="bg-gray-700 p-6 rounded-md border-2">
                    <h2 className="text-white text-lg font-bold mb-3">Profile Information</h2>
                    <p className="text-white mt-2 text-container">
                        <strong>Username:</strong>
                        <span id="username" style={{ flex: 1, overflow: 'hidden', whiteSpace: 'nowrap' }}>{username}</span>
                        <button id="username-edit-button" className="edit-button" onClick={() => openEditing('username')}>Edit</button>
                        <button id="username-save-button" style={{ display: editingField === 'username' ? 'block' : 'none' }} className="save-button" onClick={() => saveField('username')}>Save</button>
                    </p>
                    <p className="text-white mt-2 text-container">
                        <strong>Password:</strong>
                        <span id="password">{password}</span>
                        <button id="password-edit-button" className="edit-button" onClick={() => openEditing('password')}>Edit</button>
                        <button id="password-save-button" style={{ display: editingField === 'password' ? 'block' : 'none' }} className="save-button" onClick={() => saveField('password')}>Save</button>
                    </p>
                    <p className="text-white mt-2 text-container">
                        <strong>Job Title:</strong>
                        <span id="jobTitle">{jobTitle}</span>
                        <button id="jobTitle-edit-button" className="edit-button" onClick={() => openEditing('jobTitle')}>Edit</button>
                        <button id="jobTitle-save-button" style={{ display: editingField === 'jobTitle' ? 'block' : 'none' }} className="save-button" onClick={() => saveField('jobTitle')}>Save</button>
                    </p>
                    <p className="text-white mt-2 text-container">
                        <strong>Bio:</strong>
                        <span id="bio">{bio}</span>
                        <button id="bio-edit-button" className="edit-button" onClick={() => openEditing('bio')}>Edit</button>
                        <button id="bio-save-button" style={{ display: editingField === 'bio' ? 'block' : 'none' }} className="save-button" onClick={() => saveField('bio')}>Save</button>
                    </p>
                    <p className="text-white mt-2 text-container">
                        <strong>Country:</strong>
                        <span id="country">{country}</span>
                        <button id="country-edit-button" className="edit-button" onClick={() => openEditing('country')}>Edit</button>
                        <button id="country-save-button" style={{ display: editingField === 'country' ? 'block' : 'none' }} className="save-button" onClick={() => saveField('country')}>Save</button>
                    </p>
                    <p className="text-white mt-2 text-container">
                        <strong>Phone:</strong>
                        <span id="phone">{phone}</span>
                        <button id="phone-edit-button" className="edit-button" onClick={() => openEditing('phone')}>Edit</button>
                        <button id="phone-save-button" style={{ display: editingField === 'phone' ? 'block' : 'none' }} className="save-button" onClick={() => saveField('phone')}>Save</button>
                    </p>
                    <p className="text-white mt-2 text-container">
                        <strong>Email:</strong>
                        <span id="email">{email}</span>
                        <button id="email-edit-button" className="edit-button" onClick={() => openEditing('email')}>Edit</button>
                        <button id="email-save-button" style={{ display: editingField === 'email' ? 'block' : 'none' }} className="save-button" onClick={() => saveField('email')}>Save</button>
                    </p>
                </div>
            </div>
        </div>
    );
}


export default ProfileCard;
