import React, { useState } from 'react';
import { DarkModeSwitch } from 'react-toggle-dark-mode';
import { Link } from 'react-router-dom'; 
import ProfileNavbar from './profileNavbar';
import ProfileCard from './ProfileCard';

function Profile() {
    const [darkSide, setDarkSide] = useState(false); // Set initial dark mode state

    const toggleDarkMode = checked => {
        setDarkSide(checked); // Update dark mode state
    };

    return (
        <div className={darkSide ? 'dark' : ''}> {/* Apply dark mode class if darkSide is true */}
            {/* Profile Navbar */}
            <ProfileNavbar darkSide={darkSide} toggleDarkMode={toggleDarkMode} />

            {/* Dark Mode Switch */}
            <div className="dark-mode-switch">
                <DarkModeSwitch checked={darkSide} onChange={toggleDarkMode} size={36} />
            </div>

            {/* Profile Card */}
            <ProfileCard />
        </div>
    );
}

export default Profile;
